class SetQuestion{static create(t){return fetch("https://image-pr-default-rtdb.europe-west1.firebasedatabase.app/question.json",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)}).then(e=>e.json()).then(e=>(t.id=e.name,t)).then(getToLocalStorage).then(SetQuestion.renderQuestion)}static listToHTML(e){return e.length?`<ol>${e.map(e=>`<li>${e.text}</li>`).join("")}</ol>`:"<p>There is no question</p>"}static fetchQuestion(e){return e?fetch("https://image-pr-default-rtdb.europe-west1.firebasedatabase.app/question.json?auth="+e).then(e=>e.json()).then(t=>t.error?`<p>${t.error}</p>`:t?Object.keys(t).map(e=>({...t[e],id:e})):[]):Promise.resolve("<p>Wrong email or password</p>")}static renderQuestion(){const e=getQuestionFromLocalStorage(),t=document.querySelector(".questions__row"),o=document.querySelector(".questions__title");if(0<e.length){const n=e.map(function(e){return`
			<div class="questions__column">
				<div class="questions__date">${`${new Date(e.date).getDate()}.${new Date(e.date).getMonth()+1}.`+new Date(e.date).getFullYear()} ${new Date(e.date).getHours()}:${new Date(e.date).getMinutes()}</div>
				<div class="questions__item">${e.text}</div>
			</div>
			`});o.innerText="Questions",t.innerHTML=n.join("")}else o.innerText="There is no questions. Please enter your question above"}}function getToLocalStorage(e){const t=getQuestionFromLocalStorage();t.push(e),localStorage.setItem("questions",JSON.stringify(t))}function getQuestionFromLocalStorage(){return JSON.parse(localStorage.getItem("questions")||"[]")}export{SetQuestion};