function validate(p){return 5<=p.length}const popup=document.createElement("div");function modal(p,o){popup.innerHTML=`
	<div class="popup__body">
		<div class="popup__content">
			<a class="popup__close">
				x
			</a>
			<div class="popup__title">${p}</div>
			<div class="popup__content">${o}</div>
		</div>
	</div>
	`,document.body.appendChild(popup);const e=document.querySelector(".popup__close");e.addEventListener("click",()=>{popup.remove()})}popup.classList.add("popup"),popup.setAttribute("id","popup");export{validate,modal};